mapper.select:
SELECT t.create_date,t.create_user,t.update_date,t.update_user,t.id,u.user_name createUserName, t.item_name,t.item_amount,t.save_place,t.is_save48hour,t.remark FROM t_waste_disposal t left join sys_user u on u.id = t.create_user where 1 = 1
and date_format(t.create_date, '%Y-%m-%d') = date_format(#{createDate},'%Y-%m-%d')
and t.create_user = #{createUser}
